import React, { useState } from "react"; 
import Options from "./Options";

function Questions({ i, setI, q, setQ }) {
    const [addQs, setAddQs] = useState(false);
    const [qs, setQs] = useState(i.questions);

    function handleClickedQuestion(e, iq) {
        setQ(iq)
    }

    return (
        <div>
            {i.questions.map(iq => <button onClick={(e) => handleClickedQuestion(e, iq)}>{iq.text}</button> ) }
            {q ? <Options q={q} setQ={setQ} /> : null}
    </div>
    )
}


export default Questions;
