            },
            body: JSON.stringify({ rescue_id: rescue.id, user_id: msg.id, status: "Admin" })
            })
        .then((r) => r.json())
        .then((r) => {
            setUserRescues([...userRescues, r]) 
            setAdded(true)
        })
    })
}
else {
